package arjungame;

import java.awt.event.KeyEvent;
import java.awt.event.KeyListener;

public class KeyHandler implements KeyListener {
	public boolean upPressed, downPressed, leftPressed, rightPressed;
	@Override
	public void keyTyped(KeyEvent e) {// dont use this method
		// TODO Auto-generated method stub
		
	}

	@Override
	public void keyPressed(KeyEvent e) {
		int code = e.getKeyCode(); // returns number of the key that was pressed
		// if key is pressed boolean changes to true for 4 directions
		if(code == KeyEvent.VK_UP) {
		upPressed = true;
	}
		if(code == KeyEvent.VK_DOWN) {
			downPressed = true;
		}
		if(code == KeyEvent.VK_LEFT) {
			leftPressed = true;
		}
		if(code == KeyEvent.VK_RIGHT) {
			rightPressed = true;
		}
	}

	@Override
	public void keyReleased(KeyEvent e) {
		int code = e.getKeyCode();
		// if key is released boolean set to false for 
		if(code == KeyEvent.VK_UP) {
			upPressed = false;
		}
			if(code == KeyEvent.VK_DOWN) {
				downPressed = false;
			}
			if(code == KeyEvent.VK_LEFT) {
				leftPressed = false;
			}
			if(code == KeyEvent.VK_RIGHT) {
				rightPressed = false;
			}
	}

}
